import React, { useEffect, useRef, useState } from "react";
import { useParams } from "react-router-dom";
import { getSingleProfiles, reviewGet } from "../../services/business";
import { setupAxios } from "../../utils/axiosClient";
import { useDropzone } from "react-dropzone";
import SwiperCore from 'swiper';
import { Navigation } from 'swiper';
import { Swiper, SwiperSlide } from 'swiper/react';
import 'swiper/css';
import 'swiper/css/navigation';

import star from "../../assets/images/star-details.svg";
import world from "../../assets/images/world.png";
import share from "../../assets/images/share.png";
import reviewIcon from "../../assets/images/review-icon.png";
import Vector from "../../assets/images/Vector.png";
import mariaProfile from "../../assets/images/maria.png";
import worldOuter from "../../assets/images/world-outer.png";
import stars from "../../assets/images/stars.png";
import facebook from "../../assets/images/facebook.png";
import instagram from "../../assets/images/instagram.png";
import fullStar from "../../assets/images/full-star.png";
import blankStar from "../../assets/images/blank-star.png";
import linkIcon from "../../assets/images/link-icon.png";

SwiperCore.use([Navigation]);

export default function BusinessDetails() {
  const { bussiness } = useParams();
  const [base64Image, setBase64Image] = useState("");
  const [allReview, setAllReview] = useState("");
  const [profile, setProfile] = useState([]);
  const [file, setFile] = useState(null);
  const [loading, setLoading] = useState(true);
  const prevRef = useRef(null);
  const nextRef = useRef(null);

  useEffect(() => {
    getProfile();
    getReviews();
  }, []);

  const getProfile = async () => {
    setupAxios();
    try {
      const res = await getSingleProfiles(bussiness);
      setProfile(res?.data);
    } catch (error) {
      console.error(error);
    } finally {
      setLoading(false);
    }
  };

  const getReviews = async () => {
    setLoading(true);
    setupAxios();
    try {
      const res = await reviewGet(Number(bussiness));
      setAllReview(res?.data);
    } catch (error) {
      console.error(error);
    } finally {
      setLoading(false);
    }
  };

  const { getRootProps, getInputProps } = useDropzone({
    accept: "image/jpeg, image/png",
    onDrop: (acceptedFiles) => {
      const reader = new FileReader();
      reader.onloadend = () => {
        setBase64Image(reader.result);
      };
      reader.readAsDataURL(acceptedFiles[0]);
      setFile(acceptedFiles[0]);
    },
  });

  const modifyWebsiteUrl = (url) => {
    if (!url) return '#';
    return url.startsWith('http://') || url.startsWith('https://') ? url : `http://${url}`;
  };

  const getInitials = (name) => {
    const names = name.split(' ');
    if (names.length === 1) {
      return names[0].charAt(0).toUpperCase();
    } else {
      const firstInitial = names[0].charAt(0).toUpperCase();
      const lastInitial = names[names.length - 1].charAt(0).toUpperCase();
      return `${firstInitial}${lastInitial}`;
    }
  };

  return (
    <div className="container mt-32">
      <div className="mx-auto lg:flex flex-col justify-between items-center gap-4 my-10 lg:mb-10 lg:mt-20 p-4 lg:py-8 lg:px-16 rounded-[10px] border-2 w-[80%] bg-[#e7f1f7]">
        <div className="-mt-28">
          <img src={profile.logo} alt="image" className="w-[150px] rounded-full h-[150px] border-4 border-[#287BB7]" />
        </div>
        <div className="flex justify-evenly w-[70%]">
          <div className="lg:flex items-center gap-4 mb-6 lg:mb-0">
            <div>
              <h2 className="text-[28px] font-normal">
                <span className="font-bold gradient"> {profile?.name}</span>
              </h2>
              <div className="flex items-center gap-2 mt-3 lg:mt-0">
                <img src={star} alt="star" />
                <h6 className="font-normal text-[#8D8D8D]">
                  <span className="font-bold text-black">{profile?.rating || "5.0"}</span> ({profile?.review || "0 Reviews"})
                </h6>
              </div>
            </div>
          </div>
          <div className="flex gap-2 items-center">
            <a target={profile.website} href={modifyWebsiteUrl(profile.website)}>
              <img src={worldOuter} alt="world-outer" className="w-10 -mt-2" />
              <img src={world} alt="world" className="w-8 -mt-9 ml-1" />
            </a>
            <div>
              <h2 className="text-[15px] font-light leading-5">
                Live <span className="font-bold gradient"> Site </span>
              </h2>
              <p className="text-[#666666]">{profile.website}</p>
            </div>
          </div>
        </div>
        <div className="flex justify-evenly w-[60%]">
          <button className="flex items-center justify-center border bg-[#287BB7] w-[40%] h-16 p-6 rounded-[10px]"
            onClick={() => window.scrollTo(0, 3000)}>
            <span className="flex justify-center w-[100%] items-center">
              <img src={reviewIcon} alt="review-icon" className="w-16 filter invert" />
              <span className="text-white font-bold text-lg">Write Review</span>
            </span>
          </button>
          <button className="flex items-center justify-center border border-[#287BB7] w-[40%] h-16 p-6 rounded-[10px]">
            <span className="flex items-center gap-4">
              <img src={share} alt="save" />
              <span className="text-[#287BB7] font-bold text-lg">Share</span>
            </span>
          </button>
        </div>
      </div>
      <div className="flex justify-center items-center my-10 lg:my-16">
        <h2 className="text-[#000000] text-center">
          <span className="text-xl lg:text-2xl block font-bold mb-1 gradient">Followers</span>
          <span className="text-2xl lg:text-4xl font-light relative">
            <span className="gradient font-black">Top </span> Brand in one place
            <img className="absolute right-0 -bottom-5 h-[28px]" src={Vector} alt="arrow" />
          </span>
        </h2>
      </div>
      <div className="grid lg:grid-cols-2">
        <div className="flex flex-col justify-center items-center">
          <div className="my-12">
            <img src={facebook} alt="brand-icon" className="w-[150px] rounded-full h-[150px]" />
            <img src={mariaProfile} alt="brand-icon" className="-mt-8 ml-24 w-[50px] rounded-full h-[50px] border-2 border-[#287BB7]" />
          </div>
          <h2 className="text-[#000000] text-center">
            <span className="text-xl lg:text-2xl block font-bold mb-1">{profile?.name}</span>
            <span className="flex flex-col text-4xl font-light relative items-center">
              <span><span className="gradient font-black">Facebook </span>Followers</span>
              <a href={profile.facebook} target="_blank" className="flex items-center">
                <img src={linkIcon} alt="link-icon" className="w-[16px] h-[16px]" />
                <span className="text-sm mx-1 text-[#287BB7] hover:text-[#4ea0db] font-bold">Visit Facebook</span>
              </a>
            </span>
          </h2>
          <div className="flex flex-col items-center py-10">
            <h2 className="gradient font-black text-xl lg:text-4xl">{profile?.facebook_followers}</h2>
            <p className="font-light text-black">{profile?.name}</p>
          </div>
        </div>
        <div className="lg:border-l-2 border-[#287BB7]">
          <div className="flex flex-col justify-center items-center">
            <div className="my-12">
              <img src={instagram} alt="brand-icon" className="w-[150px] rounded-full h-[150px]" />
              <img src={mariaProfile} alt="brand-icon" className="-mt-8 ml-24 w-[50px] rounded-full h-[50px] border-2 border-[#287BB7]" />
            </div>
            <h2 className="text-[#000000] text-center">
              <span className="text-xl lg:text-2xl block font-bold mb-1">{profile?.name}</span>
              <span className="flex flex-col text-4xl font-light relative items-center">
                <span><span className="gradient font-black">Instagram </span>Followers</span>
                <a href={profile.instagram} target="_blank" className="flex items-center">
                  <img src={linkIcon} alt="link-icon" className="w-[16px] h-[16px]" />
                  <span className="text-sm mx-1 text-[#287BB7] hover:text-[#4ea0db] font-bold">Visit Instagram</span>
                </a>
              </span>
            </h2>
            <div className="flex flex-col items-center py-10">
              <h2 className="gradient font-black text-xl lg:text-4xl">{profile?.instagram_followers}</h2>
              <p className="font-light text-black">{profile?.name}</p>
            </div>
          </div>
        </div>
      </div>
      <div className="my-10 lg:my-20">
        <h2 className="text-[#000000] text-center">
          <span className="text-xl lg:text-2xl block font-bold mb-1 gradient">Reviews</span>
          <span className="text-4xl font-light relative">What Our Clients Say</span>
        </h2>
        <div className="flex justify-between items-center gap-6 w-full">
          <button className="swiper-button-prev" ref={prevRef}>Prev</button>
          <Swiper
            modules={[Navigation]}
            navigation={{
              prevEl: prevRef.current,
              nextEl: nextRef.current,
            }}
            onBeforeInit={(swiper) => {
              swiper.params.navigation.prevEl = prevRef.current;
              swiper.params.navigation.nextEl = nextRef.current;
            }}
          >
            {allReview?.map((review, index) => (
              <SwiperSlide key={index}>
                <div className="p-6 rounded-[10px] border border-[#E7E7E7]">
                  <div className="flex gap-4">
                    <img src={mariaProfile} alt="profile" className="w-10 rounded-full h-10" />
                    <div>
                      <h2 className="font-bold">{getInitials(review.name)}</h2>
                      <p className="text-[#8D8D8D]">{review.date}</p>
                    </div>
                  </div>
                  <p className="mt-4 font-normal text-sm">{review.comment}</p>
                  <div className="mt-4 flex items-center gap-1">
                    {Array(review.rating).fill().map((_, i) => (
                      <img key={i} src={fullStar} alt="star" className="w-4 h-4" />
                    ))}
                    {Array(5 - review.rating).fill().map((_, i) => (
                      <img key={i} src={blankStar} alt="star" className="w-4 h-4" />
                    ))}
                  </div>
                </div>
              </SwiperSlide>
            ))}
          </Swiper>
          <button className="swiper-button-next" ref={nextRef}>Next</button>
        </div>
      </div>
      <div className="mt-20">
        <div className="flex justify-center">
          <input type="file" {...getInputProps()} className="hidden" />
          <div {...getRootProps()} className="cursor-pointer border-dashed border-2 border-[#287BB7] p-6 rounded-md">
            {file ? (
              <img src={base64Image} alt="uploaded file" className="max-h-[150px]" />
            ) : (
              <p className="text-[#287BB7]">Drop image or click to upload</p>
            )}
          </div>
        </div>
      </div>
    </div>
  );
}
