import React, { useState } from "react";
import footerImg from "../../assets/images/image.png";
import bell from "../../assets/images/bell.svg";
import Vector from "../../assets/images/Vector.png";
import fb from "../../assets/images/fb.png";
import twitter from "../../assets/images/twitter.png";
import yt from "../../assets/images/yt.png";
import { Link } from "react-router-dom";
import axios from "axios";


export default function Index() {
  const currentDate = new Date();
  const year = currentDate.getFullYear();
  let [subState, setSubState] = useState(false);
  const [email, setEmail] = useState("");
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const localConfig = {
    Base_URL: "http://192.168.100.163:8000",
  };

  const defaultConfig = localConfig;

  const handleSubscribe = async () => {
    setLoading(true);
    setError(null);

    try {
      const fullUrl = `${defaultConfig.Base_URL}/api/subscribe/`;
      console.log("Sending request to", fullUrl);

      // Make the request
      const response = await axios.post(fullUrl, {
        email: email,
        subscribe_at: new Date().toISOString()
      });

      // Log the entire response object for inspection
      console.log("Response received:", response);

      // Check if the response status indicates success
      if (response.status >= 200 && response.status < 300) {
        console.log("Subscription successful");
        setSubState(true);
      } else {
        console.log("Subscription failed with status:", response.status);
        setError("Subscription failed. Please try again.");
      }
    } catch (err) {
      // Log the error details
      console.error("Error during subscription:", err);
      setError("Subscription failed. Please try again.");
    } finally {
      setLoading(false);
    }
  };


  const handleButtonClick = () => {
    setSubState(true);
    handleSubscribe();
  };
  return (
    <div className="bg-footer-image bg-no-repeat w-[100%] bg-top bg-cover">
      <div
        className="container mx-auto relative pt-10 lg:pt-28"
        data-aos="flip-right"
        data-aos-duration="1500"
      >
        <div className="flex justify-center items-center w-[100%]">
          <img
            src={footerImg}
            alt="Footer Image"
            className="h-[250px] lg:h-[auto]"
          />
        </div>
        {(subState) ?
          <div className="border border-red-500 p-10 lg:mt-32 text-center absolute inset-0 flex flex-col items-center justify-end lg:justify-center"

          >
            <h3 className="text-2xl text-[50px]"
              data-aos-delay="100"
              data-aos="zoom-out"
            >
              Thank You For
              <span className="gradient3 font-black relative text-[50px]">
                <img
                  className="flex justify-end absolute right-0 -bottom-5 h-[28px]"
                  src={Vector}
                  alt="arrow"
                />
                {" "}Subscribing
              </span>
            </h3>
          </div>
          :
          <div className="p-10 lg:mt-32 text-center absolute inset-0 flex flex-col items-center justify-end lg:justify-center" >
            <h4 className="font-bold lg:text-2xl text-xl mb-2">Subscribe Now</h4>
            <h3 className="text-2xl lg:text-4xl">
              <span className="gradient3 font-black">Get All </span> Updates And
              <span className="gradient3 font-black relative">
                <img
                  className="flex justify-end absolute right-0 -bottom-5 h-[28px]"
                  src={Vector}
                  alt="arrow"
                />
                {" "}Offers
              </span>
            </h3>
            <div className="flex items-center relative mt-6 lg:mt-10 w-[100%] lg:w-[670px] mx-auto">
              <input
                type="email"
                value={email}
                className="box-shadow text-[15px] focus:outline-none font-medium text-[#287BB7] rounded-full px-4 py-5 bg-white w-full"
                placeholder="Enter Your Email to Stay Updated"
                onChange={(e) => setEmail(e.target.value)}
              />
              <button className="bg-[#287BB7] hover:bg-[#287BB7] text-2xl font-bold px-6 py-4 text-white rounded-r-full absolute right-0 flex gap-2 items-center"
                onClick={handleButtonClick}
              >
                <img src={bell} className="h-[24px] w-[24px]" alt="bell icon" />
                {loading ? "Subscribing..." : "Subscribe"}
              </button>
              {error && <p className="text-red-500 mt-2">{error}</p>}
            </div>
          </div>
        }
      </div>
      <div className="lg:flex justify-between items-center container mx-auto py-8">
        <ul className=" flex gap-4 items-center mb-6 lg:mb-0">
          <li>
            <a href="#" className="text-[#287BB7]">
              <Link to="/" onClick={() => window.scrollTo(0, 0)}> Home</Link>
            </a>
          </li>
          <li>
            <a href="#" className="text-[#287BB7]">
              <Link to="/about" onClick={() => window.scrollTo(0, 0)}>About us</Link>
            </a>
          </li>
          <li>
            <a href="#" className="text-[#287BB7]">
              <Link to="/faqs" onClick={() => window.scrollTo(0, 0)}> Faqs</Link>
            </a>
          </li>
        </ul>
        <div className="flex gap-4 items-center">
          <div className="">
            <img
              src={fb}
              alt="footer-icon"
              className="transition ease-in-out delay-150  hover:-translate-1 hover:scale-110 duration-200 cursor-pointer"
            />
          </div>
          <div>
            <img
              src={twitter}
              alt="footer-icon"
              className="transition ease-in-out delay-150  hover:-translate-1 hover:scale-110 duration-200 cursor-pointer"
            />
          </div>
          <div>
            <img
              src={yt}
              alt="footer-icon"
              className="w-[3.5rem] transition ease-in-out delay-150  hover:-translate-1 hover:scale-110 duration-200 cursor-pointer"
            />
          </div>
        </div>
      </div>
      <div className="lg:mx-20 mx-4">
        <div className="border-t border-[#C7C7C7]"></div>
      </div>
      <div className="flex justify-between items-center px-4 lg:px-32 mx-auto py-8">
        <ul className=" flex gap-4 items-center">
          <li>
            <a href="#" className="text-[#287BB7] text-sm">
              <Link to="policy"
                onClick={() => window.scrollTo(0, 0)}
              >Privacy Policy</Link>
            </a>
          </li>
        </ul>
        <div className="text-[#287BB7] text-sm">
          <p>Copyright © {year} All rights reserved</p>
        </div>
      </div>
    </div>
  );
}
