import React, { useEffect, useState } from 'react';
import { getAllProfiles } from '../../services/business.js';
import { getRatings } from '../../services/ratings';
import Loader from '../../components/Loader/loader';
import NoData from '../../components/noData/noData';
import AOS from 'aos';
import Vector from '../../assets/images/Vector.svg';
import star from '../../assets/images/star.svg';
import world from '../../assets/images/world.svg';
import locationIcon from '../../assets/images/location.svg';

const modifyWebsiteUrl = (url) => {
  if (!url.startsWith('http://') && !url.startsWith('https://')) {
    return `http://${url}`;
  }
  return url;
};

export default function FeaturedListings() {
  const [profileData, setProfileData] = useState([]);
  const [loading, setLoading] = useState(true);

  const getProfilesWithRatings = async () => {
    try {
      const profilesResponse = await getAllProfiles();
      console.log("Fetched Profiles:", profilesResponse.data);

      const profiles = profilesResponse.data.results;

      // Fetch ratings for all profiles
      const ratingsResponse = await getRatings();
      const ratings = ratingsResponse.results;

      const profilesWithRatings = profiles.map(profile => {
        const profileRatings = ratings.filter(rating => rating.brand_profile === profile.id);
        console.log(`Fetched Ratings for Profile ID ${profile.id}:`, profileRatings);

        return {
          ...profile,
          ratings: profileRatings.length > 0 ? profileRatings : [] // Handle empty ratings
        };
      });

      console.log("Profiles with Ratings:", profilesWithRatings);

      setProfileData(profilesWithRatings);
      setLoading(false);
    } catch (error) {
      console.error('Error fetching profiles with ratings:', error);
      setLoading(false);
    }
  };

  useEffect(() => {
    getProfilesWithRatings();
  }, []);

  useEffect(() => {
    AOS.init({
      duration: 4000,
      once: true,
    });
  }, []);

  const calculateAverageRating = (ratings) => {
    if (!ratings || ratings.length === 0) return 0;
    const total = ratings.reduce((acc, rating) => acc + rating.rating, 0);
    return (total / ratings.length).toFixed(1);
  };

  return (
    <div className="container mx-auto">
      <div className="flex justify-center items-center">
        <h2 className="text-[#000000] text-center">
          <span className="text-xl lg:text-2xl block font-bold mb-1">
            Featured Listings
          </span>
          <span className="text-2xl lg:text-4xl font-semibold relative">
            <span className="gradient font-black"> Cloths Goods </span> Business
            <img className="flex justify-end absolute right-0 -bottom-5 h-[28px]" src={Vector} alt="arrow" />
          </span>
        </h2>
      </div>
      <div className="w-100 flex-wrap justify-between items-center flex gap-5 mt-12">
        {loading ? (
          <Loader />
        ) : profileData.length === 0 ? (
          <NoData />
        ) : (
          profileData.map((item) => {
            const averageRating = calculateAverageRating(item.ratings);

            return (
              <div key={item.id} data-aos="fade-right" data-aos-delay="300" className="bg-white border border-black lg:w-[30%] w-full sm:w-[45%] mt-8 h-full rounded-[10px] box-shadow2 flex max-h-[500px] min-h-[450px] justify-between flex-col pt-2 px-5 pb-5">
                <div className="">
                  <div className="flex items-center justify-center">
                    <div className="gradient-border">
                      <div className="inner-circle">
                        <img src={item.logo} alt="cloths" className="w-[100px] h-[100px] rounded-full" />
                      </div>
                    </div>
                  </div>
                  <div className="bg-[#FFF8F8] border border-border2 p-6 -mt-[50px] rounded-[20px]">
                    <h4 className="text-xl font-normal mt-14">
                      <span className="gradient font-bold"> {item.name}</span>
                    </h4>
                    <div className="flex items-center justify-between gap-4 pt-2">
                      <div className="flex gap-5 items-center">
                        <p className="gradient2 h-[45px] w-[45px] rounded text-xl font-medium text-white flex items-center justify-center">
                          {averageRating > 0 ? averageRating : 'N/A'}
                        </p>
                        <div className="">
                          <img src={star} alt="star" className="mb-1" />
                          <h4 className="text-[17px] text-[#8D8D8D] font-normal">
                            <span className="text-black font-bold">
                              {averageRating > 0 ? averageRating : 'N/A'}
                            </span>{' '}
                            ({item.ratings.length > 0 ? item.ratings.length : 'No'}{' '}
                            Reviews)
                          </h4>
                        </div>
                      </div>
                      <a target="_blank" rel="noreferrer" href={modifyWebsiteUrl(item.website)}>
                        <img src={world} alt="world" />
                      </a>
                    </div>
                  </div>
                  <div className="py-6">
                    <h6 className="text-xl font-normal flex items-center mb-4">
                      <span className="font-semibold"> {item.country} </span>
                      <img src={locationIcon} alt="location" className="ps-1" />
                    </h6>
                    <p className="text-[#8A8A8A] text-[17px]">
                      {item.description.length > 70 ? `${item.description.substring(0, 70)}...` : item.description}
                    </p>
                  </div>
                </div>
              </div>
            );
          })
        )}
      </div>
    </div>
  );
}
