import React, { useEffect, useState } from "react";
import { getAllProfiles, reviewGet } from "../../services/business";
import { setupAxios } from "../../utils/axiosClient";
import { Link, useLocation } from "react-router-dom";
import { useDebounce } from "use-debounce";
import ReactPaginate from "react-paginate";
import Loader from "../../components/Loader/loader";
import NoData from "../../components/noData/noData";
import star from "../../assets/images/star-details.svg";
import defaultImg from "../../assets/images/default-brand.png";
import linkIcon from "../../assets/images/link-icon.png";

export default function BusinessList() {
  const location = useLocation();
  const queryParams = new URLSearchParams(location.search);
  const name = queryParams.get("name");
  const category = queryParams.get("category");
  const [text, setText] = useState(name || "");
  const [value] = useDebounce(text, 1000);
  const [loading, setLoading] = useState(true);
  const [businesses, setBusinesses] = useState([]);
  const [loadingReview, setLoadingReview] = useState(false);
  const [reviewsData, setReviewsData] = useState({}); // Store reviews for each brand by ID
  const [itemOffset, setItemOffset] = useState(0);
  const [profile, setProfile] = useState([]);
  const [total, setTotal] = useState(0);
  const { bussiness } = useParams();
  const [currentPage, setCurrentPage] = useState(0);

  const handlePageClick = (event) => {
    window.scrollTo({ top: 0, behavior: 'smooth' });
    setCurrentPage(event.selected);
    const newOffset = (event.selected * 10) % profile?.length;
    setItemOffset(newOffset);
  };

  const getProfile = async () => {
    setupAxios();
    try {
      setLoading(true);
      const res = await getAllProfiles(category, currentPage + 1, 10, value);
      setTotal(res?.data?.count);
      setProfile(res?.data?.results);
      setLoading(false);
    } catch (error) {
      setLoading(false);
    }
  };

  useEffect(() => {
    getProfile();
  }, [currentPage, value]);

  const fetchReviewsForBrand = async (brandId) => {
    setupAxios();
    try {
      const res = await reviewGet(brandId);
      const reviews = res?.data?.results || [];
      const totalReviews = reviews.length;
      const averageRating = totalReviews
        ? (reviews.reduce((sum, review) => sum + review.rating, 0) / totalReviews).toFixed(1)
        : 0;

      setReviewsData((prevData) => ({
        ...prevData,
        [brandId]: { totalReviews, averageRating },
      }));
    } catch (error) {
      Promise.ret
      console.error(`Error fetching reviews for brand ${brandId}:`, error);
      setReviewsData((prevData) => ({
        ...prevData,
        [brandId]: { totalReviews: 0, averageRating: 0 },
      }));
    }
  };

  useEffect(() => {
    if (profile.length > 0) {
      profile.forEach((item) => fetchReviewsForBrand(item.id));
    }
  }, [profile]);

  const ensureProtocol = (url) => {
    if (!url) return '#';
    return url.startsWith('http://') || url.startsWith('https://') ? url : `http://${url}`;
  };

  return (
    <>
      <div className="container">
        <div className="max-w-5xl m-auto">
          {loading ? (
            <Loader />
          ) : (
            currentItems?.map((item) => {
              const displayDomain = item.website?.replace(/^https?:\/\//, '');
              const websiteURL = ensureProtocol(item.website);

              const brandRating = businessRatings[item.id] || { avgRating: "0", reviewCount: 0 };

              return (
                <div key={item.id} className="flex justify-between items-center py-4 rounded-xl mb-6 h-[180px] px-4 shadow-box-shadow min-h-[180px]">
                  <div className="flex items-center">
                    <div className="flex-shrink-0 mx-auto w-[108px] h-[108px]">
                      <img
                        src={item.logo || defaultImg}
                        className="rounded-full w-[108px] h-[108px]"
                        alt={item.name}
                        onError={(e) => { e.target.src = defaultImg; }}
                      />
                    </div>
                    <div className="flex-1 w-[85%] mx-auto pl-4">
                      <h2 className="text-xl lg:text-xl font-normal">
                        <span className="font-bold"> {item.name}</span>
                      </h2>
                      <div className="flex items-center gap-2 mt-3 lg:mt-0">
                        <img src={star} alt="star" className="mb-0" />
                        <h6 className="font-normal text-[#8D8D8D]">
                          <span className="font-bold text-black">{brandRating.avgRating}</span> ({`${brandRating.reviewCount} Reviews`})
                        </h6>
                      </div>
                      <a href={websiteURL} className="flex items-center" target="_blank" rel="noopener noreferrer">
                        <img src={linkIcon} alt="link-icon" className="w-[16px] h-[16px]" />
                        <span className="text-sm mx-1 text-[#287BB7] hover:text-[#4ea0db]">{displayDomain}</span>
                      </a>
                      <p className="mt-6">
                        {item.description?.length > 170 ? (
                          <>
                            {item.description.substring(0, 170)}...
                            <Link to={`/business-details/${item.id}`} className="text-[#287BB7] hover:text-[#4ea0db]"> Read More</Link>
                          </>
                        ) : (
                          item.description || "No Description Available"
                        )}
                      </p>
                    </div>
                  </div>
                  <div>
                    <Link to={`/business-details/${item.id}`} target="_blank" onClick={() => window.scrollTo(0, 0)}>
                      <button className="w-full px-8 py-2 bg-color rounded-md text-[17px] text-white">View</button>
                    </Link>
                  </div>
                </div>
              );
            })
          )}

          <ReactPaginate
            className="flex m-auto justify-center"
            activeClassName="bg-blue-500 !text-white"
            breakClassName="item-black border cursor-default"
            breakLabel={<span className="pointer-events-none">{" _ " + " _ " + " _ "}</span>}
            containerClassName="pagination bg-gray-100"
            marginPagesDisplayed={1}
            nextClassName="item next larger-text"
            nextLabel={<span style={{ backgroundColor: '#287BB7', color: 'white', padding: '10px 20px' }}>Next</span>}
            previousClassName="item previous"
            previousLabel={<span style={{ backgroundColor: '#287BB7', color: 'white', padding: '10px 20px' }}>Previous</span>}
            forcePage={currentPage}
            onPageChange={handlePageClick}
            pageCount={pageCount}
            pageClassName="item pagination-page border text-gray-900"
            pageLinkClassName="w-full h-full flex items-center justify-center"
            pageRangeDisplayed={3}
          />
        </div>
      </div>
    </>
  );
}
