import React, { useEffect, useState } from "react";
import ReactPaginate from "react-paginate";
import { setupAxios } from "../../../utils/axiosClient";
import { getAllProfiles } from "../../../services/business";
import { reviewGet } from "../../../services/business";
import Loader from "../../Loader/loader";
import NoData from "../../noData/noData";
import { Link } from "react-router-dom";
import star from "../../../assets/images/star-details.svg";
import defaultImg from "../../../assets/images/default-brand.png"
import linkIcon from "../../../assets/images/link-icon.png"
import { useParams } from "react-router-dom";


const PaginationItems = ({ itemsPerPage, category, value }) => {
  const { bussiness } = useParams();
  const [itemOffset, setItemOffset] = useState(0);
  const [profile, setProfile] = useState([]);
  const [loading, setLoading] = useState(true);
  const [total, setTotal] = useState(0);
  const [currentPage, setCurrentPage] = useState(0);
  const [totalReviews, setTotalReviews] = useState(0);
  const [loadingReview, setLoadingReview] = useState(false);
  const [averageRating, setAverageRating] = useState(0);
  const capitalizeWords = (str) => {
    if (!str) return '';
    return str
      .toLowerCase()
      .split(' ')
      .map((word) => word.charAt(0).toUpperCase() + word.slice(1))
      .join(' ');
  };

  const handlePageClick = (event) => {
    window.scrollTo({ top: 0, behavior: 'smooth' });
    setCurrentPage(event.selected);
    const newOffset = (event.selected * itemsPerPage) % profile?.length;
    console.log(
      `User requested page number ${event.selected}, which is offset ${newOffset}`
    );
    setItemOffset(newOffset);
  };
  const getProfile = async () => {
    setupAxios();
    try {
      setLoading(true);
      const res = await getAllProfiles(
        category,
        currentPage + 1,
        itemsPerPage,
        value
      );
      setTotal(res?.data?.count);
      setProfile(res?.data?.results);
      setLoading(false);
    } catch (error) {
      setLoading(false);
    }
  };
  const calculateAverageRating = (reviews) => {
    if (reviews.length === 0) return 0;
    const total = reviews.reduce((sum, review) => sum + review.rating, 0);
    return (total / reviews.length).toFixed(1);
  };
  const getReviews = async () => {
    // setLoadingReview(true);
    setupAxios();
    try {
      const res = await reviewGet(Number(bussiness));
      const reviews = res?.data?.results || [];

      const filteredReviews = reviews.filter(review => review.brand_profile === Number(bussiness));


      setAverageRating(calculateAverageRating(filteredReviews));
      console.log(filteredReviews);
      

      // setReviews(filteredReviews);

      // setAllReview(filteredReviews);
      // setReviewFetchDate(new Date());
      setTotalReviews(filteredReviews.length);

    } catch (error) {
      console.error(error);
    } finally {
      setLoadingReview(false);
    }
  };

  useEffect(() => {
    getProfile();
    getReviews();
  }, [currentPage, value]);
  console.log(currentPage, "page>>>>>>>>>.");
  const endOffset = itemOffset + itemsPerPage;
  console.log(`Loading items from ${itemOffset} to ${endOffset}`);
  const currentItems = profile?.slice(itemOffset, endOffset);
  const pageCount = Math.ceil(total / itemsPerPage);
  console.log(profile.length, "length>>>>>>.");
  useEffect(() => {
    const endOffset = +itemsPerPage * +currentPage + +itemsPerPage;
    const itemOffset = +itemsPerPage * +currentPage;
  }, [itemsPerPage, loading, profile, currentPage]);
  console.log(profile, ">>>>>>>>>");

  const ensureProtocol = (url) => {
    if (!url) return '#';
    return url.startsWith('http://') || url.startsWith('https://') ? url : `http://${url}`;
  };
  return (
    <>
      <div>
        <div className="max-w-5xl m-auto ">
          {loading ? (
            <Loader />
          ) : profile?.length === 0 ? (
            <NoData />
          ) : (
            currentItems?.map((item) => {
              const displayDomain = item.website.replace(/^https?:\/\//, '');
              const websiteURL = ensureProtocol(item.website);
              { console.log("this is data of profile = " + profile) }
              { console.log("this is data of currentItems = " + currentItems) }
              return (
                <div className="flex justify-between items-center py-4 rounded-xl mb-6 h-[180px] px-4 shadow-box-shadow min-h-[180px] bg-white">
                  <div className="flex items-center">
                    <div className="flex-shrink-0 mx-auto w-[108px] h-[108px]">
                      <img
                        src={item?.logo || defaultImg}
                        className="rounded-full w-[108px] h-[108px] flex items-center"
                        onError={(e) => {
                          e.target.src = defaultImg;
                        }}
                      />
                    </div>
                    <div className="flex-1 w-[85%] mx-auto pl-4">
                      <h2 className="text-xl lg:text-xl font-normal">
                        <span className="font-bold"> {capitalizeWords(item?.name)}</span>{" "}
                      </h2>
                      <div className="flex items-center gap-2 mt-3 lg:mt-0">
                        <img src={star} alt="star" className="mb-0" />
                        <h6 className="font-normal text-[#8D8D8D]">
                          <span className="font-bold text-black">5.0 </span> (34 Reviews)
                        </h6>
                      </div>
                      <div>
                        <a href={websiteURL} className="flex items-center" target="_blank">
                          <img src={linkIcon} alt="link-icon" className="w-[16px] h-[16px]" />
                          <span className="text-sm mx-1 text text-[#287BB7] hover:text-[#4ea0db]">{displayDomain}</span>
                        </a>
                      </div>
                      <p className="mt-6">
                        {item.description?.length > 170 ? (
                          <div>
                            {item.description.substring(0, 170)}
                            <a href="#" className="text-[#287BB7] hover:text-[#4ea0db]">
                              {" "}
                              ... Read More
                            </a>
                          </div>
                        ) : (
                          item.description || "No Description Available"
                        )}
                      </p>

                    </div>
                  </div>
                  <div className="">
                    <Link to={`/business-details/${item?.id}`} target="_blank" onClick={() => window.scrollTo(0, 0)}
                    >
                      <button className="w-full px-8 py-2 bg-color rounded-md text-[17px] text-white">
                        View
                      </button>
                    </Link>
                  </div>
                </div>
              );
            })
          )}
          <ReactPaginate
            className="flex m-auto justify-center"
            activeClassName="bg-blue-500 !text-white" // Active page background and text color
            breakClassName="item-black border cursor-default" // Make page break non-clickable
            breakLabel={<span className="pointer-events-none">{" _ " + " _ " + " _ "}</span>} // Use span to disable clicking
            containerClassName="pagination bg-gray-100" // Pagination container background color
            marginPagesDisplayed={1}
            nextClassName="item next larger-text" // Next button base class
            nextLabel={<span style={{ backgroundColor: '#287BB7', color: 'white', padding: '10px 20px' }}>Next</span>} // Styled Next button
            previousClassName="item previous" // Previous button base class
            previousLabel={<span style={{ backgroundColor: '#287BB7', color: 'white', padding: '10px 20px' }}>Previous</span>} // Styled Previous button
            forcePage={currentPage}
            onPageChange={handlePageClick}
            pageCount={pageCount}
            pageLinkClassName="w-full h-full flex items-center justify-center" // Make the whole box clickable
            pageClassName="item pagination-page border text-gray-900" // Page number text color
            pageRangeDisplayed={3}
          />

        </div>
        {console.log(pageCount, "count>>>>>")}
      </div>
    </>
  );
};

export default PaginationItems;
