import React, { useEffect, useState, useRef } from "react";
import { useDropzone } from "react-dropzone";
import { RotatingLines } from 'react-loader-spinner';
import { useParams } from "react-router-dom";
import {
    addReview,
    getSingleProfiles,
    reviewGet,
} from "../../services/business";
import { setupAxios } from "../../utils/axiosClient";
import { ToastContainer, toast } from "react-toastify";

const AddReview = ({ setAllReview, allReview }) => {
    const [rating, setRating] = useState("");
    const [review, setReview] = useState("");
    const [loadingReview, setLoadingReview] = useState(false);
    const [base64Image, setBase64Image] = useState("");
    const { bussiness } = useParams();
    const [file, setFile] = useState(null);
    const [loading, setLoading] = useState(true);
    const [profile, setProfile] = useState([]);

    // Ref for scrolling
    const reviewSectionRef = useRef(null);

    useEffect(() => {
        getProfile();
        getReviews();
    }, [bussiness]);

    const AddReviews = async (e) => {
        e.preventDefault();
        setLoadingReview(true);
        setupAxios();
        const userId = localStorage.getItem("user_id");

        const payload = {
            description: review,
            proof_of_order: base64Image,
            brand_profile: Number(bussiness),
            rating: rating,
            user: userId,
        };

        try {
            const reviewResponse = await addReview(payload); // Post the review
            const newReview = reviewResponse.data;  // Get the newly added review

            // Dynamically update the reviews by adding the new review
            setAllReview(prevReviews => [...prevReviews, newReview]);

            toast.success("Review sent successfully");

            // Scroll to the review section
            if (reviewSectionRef.current) {
                reviewSectionRef.current.scrollIntoView({ behavior: "smooth", block: "start" });
            }
        } catch (error) {
            if (error.response) {
                toast.error(`Error: ${error.response.data.message || "Bad Request"}`);
            } else {
                toast.error("An unexpected error occurred.");
            }
        } finally {
            setLoadingReview(false);
        }
    };

    const handleRatingChange = (e) => {
        const value = Math.max(0, Math.min(5, Number(e.target.value)));
        setRating(value);
    };

    const getProfile = async () => {
        setupAxios();
        try {
            const res = await getSingleProfiles(bussiness);
            setProfile(res?.data);
        } catch (error) {
            console.error(error);
        } finally {
            setLoading(false);
        }
    };

    const getReviews = async () => {
        setLoadingReview(true);
        setupAxios();
        try {
            const res = await reviewGet(Number(bussiness));
            setAllReview(res?.data);
        } catch (error) {
            console.error(error);
        } finally {
            setLoadingReview(false);
        }
    };

    const { getRootProps, getInputProps } = useDropzone({
        accept: "image/jpeg, image/png",
        onDrop: (acceptedFiles) => {
            const reader = new FileReader();
            reader.onloadend = () => {
                setBase64Image(reader.result);
            };
            reader.readAsDataURL(acceptedFiles[0]);
            setFile(acceptedFiles[0]);
        }
    });

    const stack2 = (
        <div
            {...getRootProps()}
            className="mt-[14px] bg-[#f4fcfc] w-full h-[142px] flex justify-center items-center rounded-md border-2 border-dotted border-primary"
        >
            <input {...getInputProps()} />
            <div className="w-full flex flex-col items-center justify-center">
                <div className="w-[36px] h-[36px]">
                    <img src="/icons/Upload icon.svg" alt="" />
                </div>
                <div className="text-[12px] font-bold leading-[18px] text-[#0F0F0F]">
                    Drag & Drop Files or <span className="text-primary">Browse</span>
                </div>
                <div className="mt-[10px] text-[10px] 2xl:text-[12px] font-normal leading-[12px] 2xl:leading-[18px] text-[#676767]">
                    {file?.name || "Supported formats: JPEG, PNG"}
                </div>
            </div>
        </div>
    );

    return (
        <div className="shadow-box-shadow rounded-3xl p-4 lg:p-10 mb-10 lg:mb-20">

            {/* Profile Section */}


            <form onSubmit={AddReviews}>
                <div className="grid lg:grid-cols-1 gap-6 lg:mb-4 mb-4">
                    <div className="w-full ">
                        <label className="block capital text-[#000] text-[15px] mb-2">
                            Choose Rate
                        </label>
                        <input
                            value={rating}
                            onChange={(e) => setRating(e.target.value)}
                            className="w-full shadow-box-shadow text-black rounded-xl border p-4 focus:outline-[#87cdff]"
                            max={5}
                            type="number"
                            placeholder="Enter your rating (0-5)"
                        />
                    </div>
                </div>
                Proof of Order
                {stack2}

                {/* File preview section */}
                {file && (
                    <div className="mt-4">
                        <p>Preview:</p>
                        <img src={base64Image} alt="Preview" className="w-[200px] h-[150px] object-cover rounded-md" />
                    </div>
                )}

                <div className="lg:mb-6 mb-10">
                    <label htmlFor="message" className="block capital text-[#000] text-[15px] mb-2">
                        Review
                    </label>
                    <textarea
                        value={review}
                        onChange={(e) => setReview(e.target.value)}
                        id="message"
                        rows="6"
                        className="w-full shadow-box-shadow text-black rounded-xl border p-4 focus:outline-[#87cdff] min-h-20 resize-none"
                        placeholder="Write your Review"
                    ></textarea>
                </div>
                {loadingReview ? (
                    <button
                        type="button"
                        className="gradient2 text-xl lg:text-[22px] flex items-center justify-center font-bold h-[60px] w-[200px] rounded-md text-white">
                        <RotatingLines
                            visible={true}
                            height="32"
                            width="32"
                            strokeColor="#aa2e2e"
                            color="#aa2e2e"
                            strokeWidth="5"
                            animationDuration="0.75"
                            ariaLabel="rotating-lines-loading"
                        />
                    </button>
                ) : (
                    <button
                        type="submit"
                        className="gradient2 text-xl lg:text-[22px] font-bold h-[60px] w-[200px] rounded-md text-white">
                        Submit Review
                    </button>
                )}
            </form>

            {/* Review Section */}
            <div id="reviewSection" ref={reviewSectionRef}>
                {/* Your reviews section here */}
            </div>
        </div>
    );
};

export default AddReview;
