sourcesContent:
import axios from 'axios';
import React, { useEffect, useRef, useState } from "react";
import star from "../../assets/images/star-details.svg";
import world from "../../assets/images/world.png";
import share from "../../assets/images/share.png";
import reviewIcon from "../../assets/images/review-icon.png";
import Vector from "../../assets/images/Vector.png";
import mariaProfile from "../../assets/images/maria.png";
import calander from "../../assets/images/calander.png";
import linkIcon from "../../assets/images/link-icon.png";
import fullStar from "../../assets/images/full-star.png";
import halfStar from "../../assets/images/half-star.png";
import worldOuter from "../../assets/images/world-outer.png";
import blankStar from "../../assets/images/blank-star.png";
import like from "../../assets/images/like.png";
import dislike from "../../assets/images/dislike.png";
import heart from "../../assets/images/heart.png";
import stars from "../../assets/images/stars.png";
import OurListed from "../Home/OurListed";
import { useParams } from "react-router-dom";
import {
  addReview,
  getSingleProfiles,
  reviewGet,
} from "../../services/business";
import { setupAxios } from "../../utils/axiosClient";
import Loader from "../../components/Loader/loader";
import { FileUploader } from "react-drag-drop-files";
import { RotatingLines } from "react-loader-spinner";
import { ToastContainer, toast } from "react-toastify";
import { CiLocationOn, CiStar } from "react-icons/ci";
import { FaStar } from "react-icons/fa";
import Man from "../../assets/images/man.jpg";
// sdfdsfdsf
import { HTTP_CLIENT } from "../../utils/axiosClient";
// sdfdsfdsf

export default function BusinessDetails() {

  const { bussiness } = useParams();
  const [base64Image, setBase64Image] = useState("");
  const [review, setReview] = useState("");
  const [allReview, setAllReview] = useState("");
  const [loadingReview, setLoadingReview] = useState(false);
  const [profile, setProfile] = useState([]);
  const [file, setFile] = useState(null);
  const [rating, setRating] = useState("");
  const [loading, setLoading] = useState(true);
  const currentDate = new Date();
  const day = currentDate.getDate();
  const year = currentDate.getFullYear();
  const monthNames = [
    "January", "February", "March", "April", "May", "June",
    "July", "August", "September", "October", "November", "December"
  ];
  const month = monthNames[currentDate.getMonth()];
  const modifyWebsiteUrl = (url) => {
    return `https://www.${url}`;
  };
  const getProfile = async () => {
    setupAxios();
    try {
      setLoading(false);
      const res = await getSingleProfiles(bussiness);
      setProfile(res?.data);
    } catch (error) {
      setLoading(false);
    }
  };
  const AddReviews = async (e) => {
    setLoadingReview(true);
    e.preventDefault();
    setupAxios();
    const payload = {
      description: review,
      proof_of_order: base64Image,
      brand_profile: bussiness * 1,
      rating: rating,
    };
    try {
      const res = await addReview(payload);
      const resp = await reviewGet(bussiness * 1);
      setAllReview(resp?.data);
      toast.success("Review Sent successfully");
      console.log(res);
      setLoadingReview(false);
    } catch (error) {
      console.log(error);
      setLoadingReview(false);
    }
  };
  const getReviews = async (e) => {
    setLoadingReview(true);
    setupAxios();
    try {
      const res = await reviewGet(bussiness * 1);
      setAllReview(res?.data);
      setLoadingReview(false);
    } catch (error) {
      setLoadingReview(false);
    }
  };
  const fileTypes = ["PNG", "JPG"];
  useEffect(() => {
    getProfile();
    getReviews();
  }, []);
  const handleChange = (file) => {
    if (file) {
      const reader = new FileReader();
      reader.onloadend = () => {
        setBase64Image(reader.result);
      };
      reader.readAsDataURL(file);
    }
    setFile(file);
  };
  const stack2 = (
    <div className="mt-[14px] bg-[#f4fcfc] w-full h-[142px] flex justify-center items-center rounded-md border-2 border-dotted border-primary">
      <div className="w-full flex flex-col items-center justify-center">
        <div className="w-[36px] h-[36px]">
          <img src="/icons/Upload icon.svg" alt="" />
        </div>
        <div className="text-[12px] font-bold leading-[18px] text-[#0F0F0F]">
          Drag & drop files or <span className="text-primary">Browse</span>
        </div>
        <div className="mt-[10px] text-[10px] 2xl:text-[12px] font-normal leading-[12px] 2xl:leading-[18px] text-[#676767]">
          {file?.name
            ? file?.name
            : "Supported formates: JPEG, PNG, GIF, MP4, PDF, PSD, AI, Word, PPT"}
        </div>
      </div>
    </div>
  );
  return (


    <>
      {/* <ToastContainer /> */}
      {(
        <div className="container mt-32">
          <div className="mx-auto lg:flex flex-col justify-between items-center gap-4 my-10 lg:mb-10 lg:mt-20 p-4 lg:py-8 lg:px-16 rounded-[10px] border-2 w-[80%] bg-[#e7f1f7]">
            <div className="-mt-28">
              {/* <img src={profile?.logo} alt="image" className="w-[100px]" /> */}
              <img src={mariaProfile} alt="image" className="w-[150px] rounded-full h-[150px] border-4 border-[#287BB7]" />
            </div>
            <div className="flex  justify-evenly w-[50%]">
              <div className="lg:flex items-center gap-4 mb-6 lg:mb-0">
                <div className="">
                  <h2 className="text-[28px] font-normal">
                    <span className="font-bold gradient"> {profile?.name} Maria.b</span> {" "}
                    Cloths{" "}
                  </h2>
                  <div className="flex items-center gap-2 mt-3 lg:mt-0">
                    <img src={star} alt="star" className="mb-0" />
                    <h6 className="font-normal text-[#8D8D8D]">
                      <span className="font-bold text-black">{profile?.rating || "5.0"}</span> ({profile?.review || "0 Reviews"})
                    </h6>
                  </div>
                </div>
              </div>
              <div className="flex gap-4 items-center ">
                <div className="flex justify-center items-center">
                  <a target="_blanck" href={modifyWebsiteUrl(profile.website)}>
                    <img src={worldOuter} alt="world-outer" className="w-10" />
                    <img src={world} alt="world" className="w-8 -mt-9 ml-1" />
                  </a>
                </div>
                <div className="">
                  <h2 className="text-[15px] font-light leading-5">
                    Live <span className="font-bold gradient"> Site </span>
                  </h2>
                  <p className="text-[#666666]">{profile?.website}mariab.ccom</p>
                </div>
              </div>
            </div>
            <div className="flex justify-evenly w-[60%]">
              <button className=" flex items-center justify-center border bg-[#287BB7] w-[40%] h-16 p-6 rounded-[10px] "
                onClick={() => window.scrollTo(0, 2400)}
              >
                <span className="flex justify-center w-[100%] items-center">
                  <img src={reviewIcon} alt="review-icon" className="w-16 filter invert" />
                  <span className="text-white font-bold text-lg">
                    Write Review
                  </span>
                </span>
              </button>
              <button className=" flex items-center justify-center border border-[#287BB7] w-[40%] h-16 p-6 rounded-[10px] ">
                <span className="flex items-center gap-4">
                  <img src={share} alt="save" />
                  <span className="text-[#287BB7] bgred font-bold text-lg">
                    Share
                  </span>
                </span>
              </button>
            </div>
          </div>
          <div className="flex justify-center items-center my-10 lg:my-16">
            <h2 className="text-[#000000] text-center">
              <span className="text-xl lg:text-2xl block font-bold mb-1 gradient">
                Followers
              </span>
              <span className="text-2xl lg:text-4xl font-light relative">
                <span className="gradient font-black">Top </span> Brand in one
                place
                <img
                  className="flex justify-end absolute right-0 -bottom-5 h-[28px]"
                  src={Vector}
                  alt="arrow"
                />
              </span>
            </h2>
          </div>
          <div className="">
            <div className="grid lg:grid-cols-2 ">
              <div className="">
                <div className="flex flex-col justify-center items-center">
                  <div className="my-12">
                    <img src={mariaProfile} alt="brand-icon" className="w-[150px] rounded-full h-[150px] ab border-4 border-[#287BB7]" />
                    <img src={mariaProfile} alt="brand-icon" className="-mt-8 ml-24 w-[50px] rounded-full h-[50px] border-2  border-[#287BB7]" />
                  </div>
                  <h2 className="text-[#000000] text-center">
                    <span className="text-xl lg:text-2xl block font-bold mb-1">
                      {profile?.name}Maria.b
                    </span>
                    <span className="flex flex-col text-4xl font-light relative items-center">
                      <span><span className="gradient font-black">Facebook </span>Followers</span>

                      {/* <img
                        className="flex justify-end absolute right-0 -bottom-5 h-[28px]"
                        src={Vector}
                        alt="arrow"
                      /> */}
                      <a href="facebook.com" className="flex items-center" target="_blank">
                        <img src={linkIcon} alt="link-icon" className="w-[16px] h-[16px]" />
                        <span className="text-sm mx-1 text text-[#287BB7] hover:text-[#4ea0db] font-bold">Visit Facebook</span>
                      </a>
                    </span>
                  </h2>
                  <div className="flex justify-center items-center flex-col py-10">
                    <h2 className="gradient font-black text-xl lg:text-4xl">
                      {profile?.facebook_followers}18,000
                    </h2>
                    <p className="font-light text-black">{profile?.name}Maria.b Followers</p>
                  </div>
                </div>
                <div className="lg:w-[80%] mx-auto box-shadow2 rounded-[10px]">
                  {/* <div className="flex items-center justify-center gap-4 p-4 box-shadow2">
                    <div className="w-36">
                      <img src={profile?.logo} alt="image" className="gradient" />
                    </div>
                    <div className="">
                      <h2 className="text-xl lg:text-4xl font-normal text-black">
                        <span className="font-bold">{profile?.name}</span> Cloths
                      </h2>
                      <div className="flex items-center gap-2 mt-3 lg:mt-0">
                        <img src={star} alt="star" className="mb-0" />
                        <h6 className="font-normal text-[#8D8D8D]">
                          <span className="font-bold text-black">
                            {" "}
                            34 Reviews{" "}
                          </span>
                          $$$$...
                        </h6>
                      </div>
                    </div>
                  </div> */}

                </div>
              </div>
              <div className="lg:border-l-2 border-[#287BB7]">
                <div className="flex flex-col justify-center items-center">
                  <div className="my-12">
                    <img src={mariaProfile} alt="brand-icon" className="w-[150px] rounded-full h-[150px] ab border-4 border-[#287BB7]" />
                    <img src={mariaProfile} alt="brand-icon" className="-mt-8 ml-24 w-[50px] rounded-full h-[50px] border-2  border-[#287BB7]" />
                  </div>                  <h2 className="text-[#000000] text-center">
                    <span className="text-xl lg:text-2xl block font-bold mb-1">
                      {profile?.name}Maria.b
                    </span>
                    <span className="flex flex-col text-2xl items-center lg:text-4xl font-light relative">
                      <span><span className="gradient font-black">Instagram </span>Followers</span>

                      {/* <img
                        className="flex justify-end absolute right-0 -bottom-5 h-[28px]"
                        src={Vector}
                        alt="arrow"
                      /> */}
                      <a href="instagram.com" className="flex items-center" target="_blank">
                        <img src={linkIcon} alt="link-icon" className="w-[16px] h-[16px]" />
                        <span className="text-sm mx-1 text text-[#287BB7] hover:text-[#4ea0db] font-bold">Visit Instagram</span>
                      </a>
                    </span>
                  </h2>
                  <div className="flex justify-center items-center flex-col py-10">
                    <h2 className="gradient font-black text-xl lg:text-4xl">
                      {profile?.insta_followers}18,000
                    </h2>
                    <p className="font-light text-black">{profile?.name}Maria.b Followers</p>
                  </div>
                </div>
                <div className="lg:w-[80%] mx-auto box-shadow2 rounded-md">
                  {/* <div className="flex items-center justify-center gap-4 p-4 box-shadow2">
                    <div className="w-36">
                      <img src={profile?.logo} alt="image" className="" />
                    </div>
                    <div className="">
                      <h2 className="text-xl lg:text-4xl font-normal text-black">
                        <span className="font-bold">{profile?.name}</span> Cloths
                      </h2>
                      <div className="flex items-center gap-2 mt-3 lg:mt-0">
                        <img src={star} alt="star" className="mb-0" />
                        <h6 className="font-normal text-[#8D8D8D]">
                          <span className="font-bold text-black">
                            {" "}
                            34 Reviews{" "}
                          </span>
                          $$$$...
                        </h6>
                      </div>
                    </div>
                  </div> */}

                </div>
              </div>
            </div>
          </div>
          <h2 className="text-4xl font-normal my-24">
            <span className="font-bold gradient"> Recommended </span> Reviews
          </h2>
          <div className="border border-black flex w-full h-[50vh] gap-6">
            <div className="border border-[red] w-[30%] rounded-3xl bg-[#287BB7]"></div>
            <div className="border border-[green] w-[70%]">
              {/* slider starts here */}
              {/* slider ends here */}
            </div>
          </div>
          {/* <div className="flex justify-center items-center my-10 lg:my-16">
            <h2 className="text-[#000000] text-center">
              <span className="text-xl lg:text-2xl block font-bold mb-1">
                Reviews
              </span>
              <span className="text-2xl lg:text-4xl font-light relative">
                <span className="gradient font-black">Top </span> Brand in one
                place
                <img
                  className="flex justify-end absolute right-0 -bottom-5 h-[28px]"
                  src={Vector}
                  alt="arrow"
                />
              </span>
            </h2>
          </div> */}

          <div className="box-shadow2 my-10 p-4 lg:p-10 rounded-[10px]">
            <div className="flex justify-evenly items-center">
              <div className="w-[50%] px-4">
                <div className="">
                  <h2 className="text-4xl font-normal">
                    <span className="font-bold gradient"> Over All </span> Rating
                  </h2>
                  <img src={stars} alt="rating-stars" className="my-3" />
                </div>
                <h6 className="text-[#737072] font-normal text-xl lg:text-2xl mb-3">
                  465 reviews
                </h6>
                <p className="text-[#BBBBBB] text-xl">
                  Lorem Ipsum is simply dummy text of the printing and
                  typesetting industry.
                </p>
              </div>
              <div className="w-[50%]  px-36">
                <div className="flex gap-8 items-center mb-8 text-2xl font-bold text-[#737072]">
                  5 Stars
                  <div className="flex justify-center items-center gap-4">
                    <img src={fullStar} alt="star-image" />
                    <img src={fullStar} alt="star-image" />
                    <img src={fullStar} alt="star-image" />
                    <img src={fullStar} alt="star-image" />
                    <img src={fullStar} alt="star-image" />
                  </div>
                </div>
                <div className="flex gap-8 items-center mb-8 text-2xl font-bold text-[#737072]">
                  4 Stars
                  <div className="flex justify-center items-center gap-4">
                    <img src={fullStar} alt="star-image" />
                    <img src={fullStar} alt="star-image" />
                    <img src={fullStar} alt="star-image" />
                    <img src={fullStar} alt="star-image" />
                    <img src={blankStar} alt="star-image" />
                  </div>
                </div>
                <div className="flex gap-8 items-center mb-8 text-2xl font-bold text-[#737072]">
                  3 Stars
                  <div className="flex justify-center items-center gap-4">
                    <img src={fullStar} alt="star-image" />
                    <img src={fullStar} alt="star-image" />
                    <img src={fullStar} alt="star-image" />
                    <img src={blankStar} alt="star-image" />
                    <img src={blankStar} alt="star-image" />
                  </div>
                </div>
                <div className="flex gap-8 items-center mb-8 text-2xl font-bold text-[#737072]">
                  2 Stars
                  <div className="flex justify-center items-center gap-4">
                    <img src={fullStar} alt="star-image" />
                    <img src={fullStar} alt="star-image" />
                    <img src={blankStar} alt="star-image" />
                    <img src={blankStar} alt="star-image" />
                    <img src={blankStar} alt="star-image" />
                  </div>
                </div>
                <div className="flex gap-8 items-center mb-8 text-2xl font-bold text-[#737072]">
                  1 Stars
                  <div className="ml-[6px] flex justify-center items-center gap-4">
                    <img src={fullStar} alt="star-image" />
                    <img src={blankStar} alt="star-image" />
                    <img src={blankStar} alt="star-image" />
                    <img src={blankStar} alt="star-image" />
                    <img src={blankStar} alt="star-image" />
                  </div>
                </div>
              </div>
            </div>
          </div>

          {/* <div> */}
          <div className="my-10 flex-col w-[94%] mx-auto px-3 text-justify">
            <div className="flex justify-between">
              <div className="flex ">
                {/* <img
                    src={
                      allReview?.proof_of_order ? allReview?.proof_of_order : Man
                    }
                    alt="man"
                    className="w-[70px] h-[70px] rounded-full border-[#287BB7] mx-4"
                  /> */}

                <div>
                  <label className="ml-1 font-bold text-xl">Kyle.banson</label>
                  {/* <div className="flex items-center gap-[2px]">
                      <CiLocationOn />
                      <small>Lahore, Pakistan</small>
                    </div> */}
                  <div className="flex gap-1 ml-1 mt-1">
                    <img src={fullStar} alt="star-icon" className="w-4" />
                    <img src={fullStar} alt="star-icon" className="w-4" />
                    <img src={fullStar} alt="star-icon" className="w-4" />
                    <img src={fullStar} alt="star-icon" className="w-4" />
                    <img src={fullStar} alt="star-icon" className="w-4" />
                  </div>
                  <p className="text-[#888686] mt-3">
                    {allReview?.description}
                  </p>
                </div>
              </div>
              <p className="text-[#888686] flex justify-center"><img src={calander} alt="calander-icon" className="w-6 h-5 mt-[1px] mr-1" />{day}-{month}-{year}</p>
            </div>
            <div className="ml-1 my-2 text-lg text-[#888686]">
              <p>Lorem ipsum dolor sit amet consectetur adipisicing elit. Et earum nihil quisquam, soluta explicabo quam suscipit harum aperiam nostrum labore libero odit possimus repellat ut. Iure exercitationem consequatur libero saepe, inventore aliquid totam incidunt? Lorem ipsum dolor, sit amet consectetur adipisicing elit. Vel, vitae sed? Nam officiis dolor ipsa placeat non, eaque quam error numquam eligendi, facere exercitationem? Lorem ipsum dolor sit amet, consectetur adipisicing elit. Quibusdam, provident. Itaque laboriosam eveniet aperiam nesciunt.</p>
            </div>
            <div className="flex text-lg text-[#515151] font-bold">
              <div className="flex justify-start items-center ">
                <img src={like} alt="heart-icon" className="w-6 h-6 m-1" />153
              </div>
              <div className="flex justify-start items-center ml-1">
                <img src={dislike} alt="heart-icon" className="w-6 h-6 m-1" />18
              </div>
            </div>
          </div>
          {/* </div> */}
          <div className="">
            <h3 className="text-xl lg:text-3xl mt-28 mb-8">
              <span className="font-black gradient "> Drop </span>
              <span className=" font-bold">Your Review</span>
            </h3>
            <div className="shadow-box-shadow rounded-3xl p-4 lg:p-10 mb-10 lg:mb-20">
              <form className="" onSubmit={AddReviews}>
                <div className="grid lg:grid-cols-1 gap-6 lg:mb-4 mb-4">
                  <div className="w-full ">
                    <label
                      className="block capital text-[#000] text-[15px] mb-2"
                      for="your-name"
                    >
                      Choose Rate
                    </label>
                    <input
                      value={rating}
                      onChange={(e) => setRating(e.target.value)}
                      className="w-full shadow-box-shadow text-black rounded-xl border p-4 focus:outline-[#87cdff]"
                      id="your-name"
                      max={5}
                      type="number"
                      placeholder="Enter your rating (0-5)"
                    />
                  </div>
                </div>
                <div className="grid lg:grid-cols-2 gap-6 lg:mb-0 mb-4">
                  <div className="w-full mb-6">
                    <label
                      className="block capital text-[#000] text-[15px] mb-2"
                      for="your-email"
                    >
                      Name
                    </label>
                    <input
                      className="w-full shadow-box-shadow text-black rounded-xl border p-4 focus:outline-[#87cdff]"
                      id="your-email"
                      type="text"
                      placeholder="Enter Your Name"
                    />
                  </div>
                  <div className="w-full">
                    <label
                      className="block capital text-[#000] text-[15px] mb-2"
                      for="your-email"
                    >
                      Email
                    </label>
                    <input
                      className="w-full shadow-box-shadow text-black rounded-xl border p-4 focus:outline-[#87cdff]"
                      id="your-email"
                      type="email"
                      placeholder="Enter Your Mail"
                    />
                  </div>
                </div>
                <FileUploader
                  multiple={false}
                  label="upload here"
                  className="w-full"
                  handleChange={handleChange}
                  name="file"
                  types={fileTypes}
                  children={stack2}
                />
                <div className="lg:mb-6 mb-10">
                  <label
                    for="message"
                    className="block capital text-[#000] text-[15px] mb-2">
                    Review
                  </label>
                  <textarea
                    value={review}
                    onChange={(e) => setReview(e.target.value)}
                    id="message"
                    rows="4"
                    className="w-full shadow-box-shadow text-black rounded-xl border p-4 focus:outline-[#87cdff] min-h-20 resize-none"

                    placeholder="Write your Review"
                  ></textarea>
                </div>
                {loadingReview ? (
                  <button
                    type="button"
                    className="gradient2 text-xl lg:text-[22px] flex items-center justify-center font-bold h-[60px] w-[200px] rounded-md text-white">
                    <RotatingLines
                      visible={true}
                      height="32"
                      width="32"
                      strokeColor="#aa2e2e"
                      color="#aa2e2e"
                      strokeWidth="5"
                      animationDuration="0.75"
                      ariaLabel="rotating-lines-loading"
                      wrapperStyle={{}}
                      wrapperClass=""
                    />
                  </button>
                ) : (
                  <button
                    type="submit"
                    className="gradient2 text-xl lg:text-[22px] font-bold h-[60px] w-[200px] rounded-md text-white">
                    Submit Review
                  </button>
                )}
              </form>
            </div>
          </div>
        </div>
      )}
      <OurListed />
    </>
  );
}
