import React, { useEffect, useState } from "react";
import Vector from "../../assets/images/Vector.png";
import star from "../../assets/images/star.svg";
import world from "../../assets/images/world.png";
import location from "../../assets/images/location.png";
import { HTTP_CLIENT } from "../../utils/axiosClient"; // Directly using HTTP_CLIENT without setupAxios
import { getAllProfiles, reviewGet } from "../../services/business";
import Loader from "../../components/Loader/loader";
import NoData from "../../components/noData/noData";
import AOS from "aos";
import { Link } from "react-router-dom";

export default function FeaturedListings() {
  const [profiles, setProfiles] = useState([]);
  const [loading, setLoading] = useState(true);

  const getProfileWithReviews = async () => {
    try {
      setLoading(true);
      const res = await HTTP_CLIENT.get('/api/profile/');
      const profiles = res?.data?.results || [];

      const profilesWithReviews = await Promise.all(
        profiles.map(async (profile) => {
          try {
            const reviewResponse = await reviewGet(profile.id);
            const reviews = reviewResponse?.data?.results || [];
            const rating = reviews.length
              ? (reviews.reduce((acc, review) => acc + review.rating, 0) / reviews.length).toFixed(1)
              : "0";

            return {
              ...profile,
              reviews,
              rating,
            };
          } catch (error) {
            console.error(`Error fetching reviews for profile ${profile.id}: `, error);
            return {
              ...profile,
              reviews: [],
              rating: "0",
            };
          }
        })
      );

      console.log("Profiles with Reviews fetched: ", profilesWithReviews);
      setProfiles(profilesWithReviews);
    } catch (error) {
      console.error("Error fetching profiles with reviews: ", error);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    getProfileWithReviews();
  }, []);

  useEffect(() => {
    AOS.init({
      duration: 4000,
      once: true,
    });
  }, []);

  const capitalizeWords = (str) => {
    if (!str) return '';
    return str
      .toLowerCase()
      .split(' ')
      .map((word) => word.charAt(0).toUpperCase() + word.slice(1))
      .join(' ');
  };

  const modifyWebsiteUrl = (url) => {
    // if (!url.startsWith('https://')) {
    return `https://${url}`;
    // }
    // return url;
  };

  return (
    <div className="container mx-auto ">
      <div className="flex justify-center items-center">
        <h2 className="text-[#000000] text-center">
          <span className="text-xl lg:text-2xl block font-bold mb-1">
            Featured Listings
          </span>
          <span className="text-2xl lg:text-4xl font-semibold relative">
            <span className="gradient font-black"> Cloths Goods </span> Business
            <img
              className="flex justify-end absolute right-0 -bottom-5 h-[28px]"
              src={Vector}
              alt="arrow"
            />
          </span>
        </h2>
      </div>

      <div className="w-100 flex-wrap justify-between items-center flex gap-5 mt-12 ">
      {/* <div className="w-100 flex-wrap justify-between items-center flex gap-5 mt-12 "> */}
        {loading ? (
          <Loader />
        ) : profiles.length === 0 ? (
          <NoData />
        ) : (
          profiles.map((profile) => {
            const reviewCount = profile.reviews.length || 0;
            return (
              <div
                key={profile.id}
                data-aos="fade-right"
                data-aos-delay="300"
                className="bg-white border lg:w-[30%] w-full sm:w-[45%] mt-8 h-full rounded-[10px] shadow-box-shadow flex max-h-[470px] min-h-[450px] justify-between flex-col pt-2 px-5 pb-5 hover:border-[#95d3ff] "
              >
                <div className="">
                  <div className="flex items-center justify-center">
                    <div className="gradient2-border p-1">
                      <img
                        src={profile.logo}
                        alt="cloths"
                        className="w-[100px] h-[100px] rounded-full"
                      />
                    </div>
                  </div>
                  <div className="bg-[#F5FBFF] border-border2 p-6 -mt-[50px] rounded-[20px] border-2">
                    <h4 className="text-xl font-normal pt-16">
                      <span className="gradient font-bold"> {capitalizeWords(profile.name)}</span>
                    </h4>
                    <div className="flex items-center justify-between gap-4 pt-2">
                      <div className="flex gap-5 items-center">
                        <p className="gradient2 h-[45px] w-[45px] rounded text-xl font-medium text-white flex items-center justify-center">
                          {profile.rating}
                        </p>
                        <div className="">
                          <img src={star} alt="star" className="mb-1" />
                          <h4 className="text-[17px] text-[#8D8D8D] font-normal">
                            ({reviewCount} Review{reviewCount !== 1 ? 's' : ''})
                          </h4>
                        </div>
                      </div>
                      <a target="_blank" href={modifyWebsiteUrl(profile.website)}>
                        <img src={world} alt="website" className="w-10 rounded-lg" />
                      </a>
                    </div>
                  </div>
                  <div className="py-6">
                    <h6 className="text-xl font-normal flex items-center mb-4">
                      <span className="font-semibold"> {profile.country} </span>
                      <img src={location} alt="location" className="ps-1" />
                    </h6>
                    <p className="text-[#8A8A8A] text-[17px] overflow-hidden">
                      {profile.description?.length > 70
                        ? `${profile.description.substring(0, 70)} ...`
                        : profile.description || "No description available"}
                    </p>
                    <Link to={`/business-details/${profile.id}`} onClick={() => window.scrollTo(0, 0)}>
                      <button className="gradient2 text-xl lg:text-2xl p-3 rounded-md text-white mt-4 w-full mb-0 ">
                        View Details
                      </button>
                    </Link>
                  </div>
                </div>
              </div>
            );
          })
        )}
      </div>
    </div>
  );
}
