import React, { useState } from "react";
import google from "../../assets/images/google.svg";
import showPassword from "../../assets/images/showPassword.png";
import hidePassword from "../../assets/images/hidePassword.png";
import axios from "axios";
import { Link, useNavigate } from "react-router-dom";

export const SignIn = () => {
  const [showPassword1, setShowPassword1] = useState(false);
  const [formValues, setFormValues] = useState({ email: "", password: "" });
  const navigate = useNavigate();

  const handleChange = (e) => {
    setFormValues({
      ...formValues,
      [e.target.name]: e.target.value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    console.log("Data sent to server:", formValues);

    try {
      const response = await axios.post(
        "https://myapi.brandsearchengine.com/api/auth/login/",
        formValues
      );

      console.log("Response from server:", response.data);

      const { access, refresh } = response.data;

      if (access && refresh) {
        localStorage.setItem("access_token", access);
        localStorage.setItem("refresh_token", refresh);
        localStorage.setItem("user_id", user_id);
        console.log("User ID stored: ", user_id);
        console.log("Tokens stored in localStorage");

        navigate("/"); // Navigate to the home page or dashboard after successful login
      } else {
        console.error("Token not provided in response.");
      }
    } catch (error) {
      console.error("Login failed:", error);
    }
  };

  return (
    // bg-[#f4fbff]
    <div className="px-4 lg:px-10 mx-auto py-8  text-center">
      {/* <p className="text-3xl font-[900] mx-auto mb-10">
        Find Reviews, Share Yours, and Discover Companies.
      </p> */}
      <div className="shadow-box-shadow pt-4 max-w-3xl mx-auto rounded-[10px] h-[75vh] bg-white">
        <div className="flex flex-col items-center w-[70%] mx-auto">
          <h2 className="pt-5 mb-6">
            <span className="text-2xl font-semibold">
              <span className="gradient font-black">Sign In</span>
            </span>
          </h2>
          <form className="w-full flex flex-col" onSubmit={handleSubmit}>
            <button
              className="flex mx-auto items-center justify-center gap-4 px-4 py-4 font-medium text-lg border rounded-full w-[95%] shadow-box-shadow"
              type="button"
            >
              <img src={google} alt="google" className="w-8" /> Continue With Google
            </button>
            <p className="text-xl mx-auto mt-3">or</p>
            <div className="p-2">
              <div className="flex flex-col w-full">
                <input
                  type="text"
                  name="email"
                  placeholder="Enter Email"
                  className="border rounded-xl p-4 m-1"
                  value={formValues.email}
                  onChange={handleChange}
                />
                <div className="relative">
                  <input
                    type={showPassword1 ? "text" : "password"}
                    name="password"
                    placeholder="Enter Password"
                    className="border rounded-xl p-4 m-1 w-full"
                    value={formValues.password}
                    onChange={handleChange}
                  />
                  <img
                    src={showPassword1 ? hidePassword : showPassword}
                    alt="toggle-password1"
                    className="w-6 absolute top-4 right-4 cursor-pointer"
                    onClick={() => setShowPassword1(!showPassword1)}
                  />
                </div>
              </div>
            </div>
            <button
              className="gradient2 rounded-full font-bold text-white px-4 py-4 mx-auto w-72"
              type="submit"
            >
              Sign In
            </button>
          </form>
          <h4 className="text-[#686868] font-xl m-3">
            Don't Have An Account?
            <span className="gradient">
              <Link to="/signup"> Sign Up </Link>
            </span>
          </h4>
        </div>
      </div>
    </div>
  );
};
