import React, { useEffect, useState } from "react";
import cloths from "../../assets/images/cloths.png";
import bgCategories from "../../assets/images/bg-categories.png";
import { getAllCategories } from "../../services/categories";
import { setupAxios } from "../../utils/axiosClient";
import Loader from "../../components/Loader/loader";
import NoData from "../../components/noData/noData";
import { Link } from "react-router-dom";

export default function PopularCategories() {
  const [category, setCategory] = useState([]);
  const [loading, setLoading] = useState(true);

  const capitalizeWords = (str) => {
    if (!str) return '';
    return str
      .toLowerCase()
      .split(' ')
      .map((word) => word.charAt(0).toUpperCase() + word.slice(1))
      .join(' ');
  };

  const getCategories = async () => {
    setupAxios();
    try {
      setLoading(false);
      const res = await getAllCategories();
      setCategory(res?.data?.results);
    } catch (error) {
      setLoading(false);
    }
  };

  useEffect(() => {
    getCategories();
  }, []);

  return (
    <div className="container h-[40vh] ">
      {category?.length === 0 ? (
        <NoData />
      ) : (
        <div className="flex flex-wrap md:flex-col sm:flex-col ">
          <img src={bgCategories} alt="Categories-Image" className="" />
          <div
            className="absolute flex mt-20 "
          // className="grid md:grid-cols-4 lg:grid-cols-4 2xl:grid-cols-6 gap-8 py-6 lg:py-10 my-10 lg:absolute "
          >
            {loading ? (
              <Loader />
            ) : (
              category?.map((item, index) => {

                return (
                  <div className="bg-red-600 w-full">
                    <Link
                      key={item?.id}
                      to={`/businessList?category=${item?.name}`}
                      onClick={() => window.scrollTo(0, 0)}
                      className="w-40 mt-16 rounded-3xl ml-6 shadow-box-shadow"
                    >
                      <div
                        className="bg-white max-h-[220px] min-h-[220px] rounded-3xl box-shadow2 flex items-center justify-center flex-col p-8 w-44 "
                      >
                        <div className="flex justify-center items-center w-full h-full">
                          <img src={cloths} alt="cloths" className="mb-4" />
                        </div>
                        <div className="">
                          <h2 className="text-2xl text-center font-bold mb-1 gradient w-full h-full flex justify-center items-center">
                            {capitalizeWords(item?.name)}
                          </h2>
                          <p className="text-[#9B9B9B] mx-auto text-center text-[15px] font-medium">
                            607 Items
                          </p>
                        </div>
                      </div>
                    </Link>
                  </div>
                );
              })
            )}
          </div>
        </div>
      )}
    </div>
  );
}
